import React from 'react'
import ActionButton from './ActionButton'

class SignInForm extends React.Component {

   constructor(){
      super();
      this.state = {
         avatar: "guest"
      }
   }

   componentDidMount(){
      const container = document.getElementsByClassName("container");
      const icons = document.getElementsByClassName("icon");
      container[0].classList.add("container-active");
      for(let i = 0; i < container.length; i++){
         container[i].addEventListener('click', () => {
            this.setState({
               avatar: icons[i].alt
            });
            container[i].classList.add("container-active");
            for(let j = 0; j < container.length; j++){
               if(i !== j)
                  container[j].classList.remove("container-active");
            }
            // console.log(this.state.avatar)
         });
      }
   }

   icon(name){
      const src = "icons/" + name + ".png";
      return (
         <div className="container">
            <img src={src} className="icon" alt={name}/>
            <div className="middle">
               <div className="text">{name}</div>
            </div>
         </div>
      )
   }

   iconPanel(){
      return (
         <div className="icon-panel">
            {this.icon("guest")}
            {this.icon("courier")}
            {this.icon("owner")}
         </div>
      )
   }

   signInForm(){
      return (
         <form className="signin-form">
            <div className="inputs">
               <label>
                  First name:
                  <input type="text" name="firstname"/>
               </label>
               <label>
                  Last name:
                  <input type="text" name="lastname"/>
               </label>
               <label>
                  Username:
                  <input type="text" name="username"/>
               </label>
               <label>
                  Password:
                  <input type="password" name="password"/>
               </label>
               <label>
                  Password again:
                  <input type="password" name="password-again"/>
               </label>
               <label>
                  E-mail:
                  <input type="text" name="email"/>
               </label>
            </div>
            <ActionButton id="signin-action" name="Sign In" url=""/>
            <div className="specData">
               {this.specData()}
            </div>
         </form>
      )
   }

   specData(){
      if(this.state.avatar === "guest")
         return (this.specDataGuest())
      if(this.state.avatar === "courier")
         return (this.specDataCourier())
      if(this.state.avatar === "owner")
         return (this.specDataOwner())

   }

   specDataGuest(){
      return (
         <div className="bonus-inputs">
            <label>
               Phone:
               <input type="text" name="phonenumber"/>
            </label>
            <label>
               Zip code:
               <input type="number" name="zipcode"/>
            </label>
            <label>
               Address:
               <input type="text" name="address"/>
            </label>
         </div>
      )
   }

   specDataCourier(){
      return (
         <div className="bonus-inputs">
            <label>
               Phone:
               <input type="text" name="phonenumber"/>
            </label>
            <label>
               Territory:
               <input type="text" name="territory"/>
            </label>
         </div>
      )
   }

   specDataOwner(){
      return (
         <div className="bonus-inputs">
            <label>
               Phone:
               <input type="text" name="phonenumber"/>
            </label>
         </div>
      )
   }

   render(){
      return (
         <div className="signin">
            {this.iconPanel()}
            {this.signInForm()}
         </div>
      )
   }
}

export default SignInForm
